var udp = require('udp-request');
var socket = udp();

socket.on('request', function (request, peer) {
  console.log('request:', request.toString())
  socket.response('echo: ' + request.toString(), peer)
})

socket.listen(8889, function () {
  socket.request('command', {port: 8889, host: '192.168.0.10'}, function (err, response) {
    if (err) throw err
    console.log('response', response.toString())
    socket.destroy()
  })
})
